mapper.select:
select count(*) from tbl_activity


        name like '%' #{name} '%'


        and owner like '%' #{owner} '%'


        and startDate > #{startDate}


        and endDate < #{endDate}

select
id, owner, name, startDate, endDate
from tbl_activity
name like '%' #{name} '%'
and owner like '%' #{owner} '%'
and startDate > #{startDate}
and endDate < #{endDate}
/*按照时间线排序*/ order by createTime desc
limit #{skipCount},#{pageSize}

mapper.delete:
delete from tbl_activity
where id in
/*遍历*/

    #{id}

mapper.update:
update tbl_activity set
owner=#{owner}, name=#{name}, startDate=#{startDate}, endDate=#{endDate}, cost=#{cost}, description=#{description}, editTime=#{editTime}, editBy=#{editBy}
where id=#{id}

mapper.select:
select car.id as id, a.owner, a.name, a.startDate, a.endDate
from tbl_activity a join tbl_clue_activity_relation car on a.id=car.activityId
where car.clueId=#{clueId}
